import {Component, OnInit} from '@angular/core';
import { Product, products } from '../products';
import { ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit{
  ngOnInit() {
    // First get the product id from the current route.
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));

    // Find the product that correspond with the id provided in route.
    this.product = products.find(product => product.id === productIdFromRoute);
  }
  product: Product | undefined;

  constructor(private route: ActivatedRoute) { }

}
<h1>Select Category</h1>
    <button type="button">
        Laptop
        <app-product-list *ngFor="let product of products" ,*ngIf="product.category =='Laptop'"></app-product-list>
    </button>
    <button type="button">
        Smartphone
        <app-product-list product.category =='Smartphone'></app-product-list>
    </button>
    <button type="button">
        Earpiece
        <app-product-list product.category =='Earpiece'></app-product-list>
    </button>
    <button type="button">
        Ipad
        <app-product-list product.category =='Ipad'></app-product-list>
    </button>
