export class  CategoryComponent implements OnInit{
  categories = categories;
  choice = '';
  products=products;
  newItem: Product[];

  constructor() {
    this.newItem = [];
    // this.currentTask = new Task('');
  }
  ngOnInit(): void {
  }
  selectItem(name:string) {
    this.choice = name;
  }
  onNewItemRemove(index: number) {
    this.newItem = this.products.filter((x) => x.id !== index);
  }

}
